//     exit;
// }

// // ID check
// if (!isset($_GET['id']) || !is_numeric($_GET['id'])) {
//     header("Location: customers.php");
//     exit;
// }


$_SESSION['loggedin'] = true;
if (!isset($_SESSION['loggedin']) || $_SESSION['loggedin'] !== true) {
    header("Location: login.php"); 
     exit; 
}


$id = intval($_GET['id']);

// Fetch current customer data
$stmt = $conn->prepare("SELECT * FROM customers WHERE id = ?");
$stmt->bind_param("i", $id);
$stmt->execute();
$result = $stmt->get_result();
$customer = $result->fetch_assoc();

if (!$customer) {
    echo "Customer not found.";
    exit;
}

// Pre-fill phone (remove country code if present, assume +91)
$full_phone = $customer['phone'] ?? '';
$phone_number = preg_replace('/^\+91/', '', $full_phone); // Remove +91 if present

// Update form
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $firstname = trim($_POST['firstname']);
    $lastname = trim($_POST['lastname']);
    $email = trim($_POST['email']);
    $phone_post = trim($_POST['phone']);
    $state = trim($_POST['state']);
    $city = trim($_POST['city']);
    $zip = trim($_POST['zip']);
    $dob = trim($_POST['dob']);

    // Hardcode Indian country code +91
    $full_phone_post = '+91' . $phone_post;

    // Profile picture
    $profile_picture = $customer['profile_picture']; // default old pic
    if (!empty($_FILES['profile_picture']['name'])) {
        $target_dir = "uploads/";
        if (!is_dir($target_dir)) {
            mkdir($target_dir, 0777, true);
        }

        $file_name = time() . "_" . basename($_FILES["profile_picture"]["name"]);
        $target_file = $target_dir . $file_name;

        if (move_uploaded_file($_FILES["profile_picture"]["tmp_name"], $target_file)) {
            $profile_picture = $file_name;
        }
    }

    // Update query
    $stmt = $conn->prepare("UPDATE customers 
        SET firstname=?, lastname=?, email=?, phone=?, state=?, city=?, zip=?, dob=?, profile_picture=? 
        WHERE id=?");
    $stmt->bind_param("sssssssssi", $firstname, $lastname, $email, $full_phone_post, $state, $city, $zip, $dob, $profile_picture, $id);
    $stmt->execute();

    header("Location: manage-customers.php?updated=1");
    exit;
}
