                citiesByState[selectedState].forEach(city => {
                    const option = document.createElement("option");
                    option.value = city;
                    option.textContent = city;
                    if (city === "<?= htmlspecialchars($customer['city'] ?? '') ?>") {
                        option.selected = true;
                    }
                    citySelect.appendChild(option);
                });
            }
